import instance from "@/util/axiosUtil";

const request = {
    login(username, password, remember) {
        return instance({
            method: 'get',
            url: '/login',
            headers: {
                "Content-Type": "application/json"
            },
            params: {
                username: username,
                password: password,
                remember: remember
            }
        })
    }
    ,
    initMenu(id) {
        return instance({
            method: 'get',
            url: '/menu/init',
            headers: {
                "Content-Type": "application/json"
            },
            params: {
                id: id
            }
        })
    }
    ,
    // 获取用户信息
    getUserInfo(id) {
        return instance({
            method: 'get',
            url: '/getUser',
            headers: {
                "Content-Type": "application/json"
            },
            params: {
                id: id
            }
        })
    }
    ,
    // 退出登录
    loginOut(id) {
        return instance({
            method: 'get',
            url: '/loginOut',
            headers: {
                "Content-Type": "application/json"
            },
            params: {
                id: id
            }
        })
    },
    // 修改登录用户
    updateUser(user) {
        return instance({
            method: 'post',
            url: '/updateUser',
            headers: {
                "Content-Type": "application/json"
            },
            data:user
        })
    },
    // 渲染每天登录人数
    getCount(){
        return instance({
            method: 'get',
            url: '/getCount',
            headers: {
                "Content-Type": "application/json"
            }
        })
    }
}

export default request;
