import router from "@/router";

export function addRoutes(routes) {
    routes.forEach(route => {
        const {path, component, name, children} = route;
        if (children && children.length > 0) {
            addRoutes(children);
        }

        let routeItem = {
            path: path,
            name: name,
            component: () => {
                if (component.startsWith("Per")) {
                    return import(`@/components/per/${component}.vue`)
                } else if (component.startsWith("Dep")) {
                    return import(`@/components/dep/${component}.vue`)
                } else if (component.startsWith("Pos")) {
                    return import(`@/components/pos/${component}.vue`)
                } else if (component.startsWith("Emp")) {
                    return import(`@/components/emp/${component}.vue`)
                } else if (component.startsWith("Log")) {
                    return import(`@/components/log/${component}.vue`)
                } else if (component.startsWith("Onb")) {
                    return import(`@/components/obb/${component}.vue`)
                } else if (component.startsWith("Ann")) {
                    return import(`@/components/anno/${component}.vue`)
                } else if (component.startsWith("Chat")) {
                    return import(`@/components/chat/${component}.vue`)
                }
            },
        };
        console.log("---")
        router.addRoute(routeItem);
    })
}
