import Home from '../components/Home.vue'
import PerConfig from '../components/per/PerConfig.vue'
import DepSetting from '../components/dep/DepSetting.vue'
import Announcement from '../components/anno/Announcement.vue'
import Chat from '../components/chat/Chat.vue'
import EmpBasic from '../components/emp/EmpBasic.vue';
import EmpAdvanch from '../components/emp/EmpAdvanch.vue';
import LogSet from '../components/log/LogSet.vue';
import LoginLog from '../components/log/LoginLog.vue';
import LogAccess from '../components/log/LogAccess.vue';
import Onboard from '../components/onbo/Onboard.vue';
import PosSetting from '../components/per/PosSetting.vue';
import router from "@/router";


export function addRoutes(routes) {
    const parentRoute = {
        path: '/home',
        name: 'Home',
        component:Home,
        children: []
    };
    for (let i = 0; i < routes.length; i++) {
        const parent = routes[i];
        if (parent.children && parent.children.length > 0) {
            for (let j = 0; j < parent.children.length; j++) {
                const child = parent.children[j];
                const childRoute = {
                    path: child.path,
                    component: null,
                    name: child.name
                };
                switch (child.component) {
                    case 'PerConfig':
                        childRoute.component = PerConfig;
                        break;
                    case 'DepSetting':
                        childRoute.component = DepSetting;
                        break;
                    case 'Announcement':
                        childRoute.component = Announcement;
                        break;
                    case 'Chat':
                        childRoute.component = Chat;
                        break;
                    case 'EmpBasic':
                        childRoute.component = EmpBasic;
                        break;
                    case 'EmpAdvanch':
                        childRoute.component = EmpAdvanch;
                        break;
                    case 'LogSet':
                        childRoute.component = LogSet;
                        break;
                    case 'LoginLog':
                        childRoute.component = LoginLog;
                        break;
                    case 'LogAccess':
                        childRoute.component = LogAccess;
                        break;
                    case 'Onboard':
                        childRoute.component = Onboard;
                        break;
                    case 'PosSetting':
                        childRoute.component = PosSetting;
                        break;
                }
                parentRoute.children.push(childRoute);
            }
        }
    }
    router.addRoute(parentRoute);
}
