export function addRoutes(routes) {
    let rou = [];
    for (let i = 0; i < routes.length; i++) {
        let {name, path, component, children} = routes[i];
        console.log(name);
        if (children && children instanceof Array) {
            children=addRoutes(routes[i].children);
        }
        let routeItem = {
            path: path,
            name: name,
            children: children,
            component: () => {
                if (component.startsWith("Home")) {
