                } else if (component.startsWith("Ann")) {
                    return import(`@/components/anno/${component}.vue`)
                } else if (component.startsWith("Chat")) {
                    return import(`@/components/chat/${component}.vue`)
                }
            },
        };
        rou.push(routeItem);
        console.log(rou);
}
    return rou;

